import '../assets/styles/HomePage/homeItems.css'
import { usePopUpContext } from '../contexts/PopUpContext'
import CardPopUp from './CardPopUps/CardPopUp'
import CardPopUpVideo from './CardPopUps/CardPopUpVideo'
interface iCardDetailsProps {
    projectID: number,
    title: string,
    description: string
}

function Card(props: iCardDetailsProps): JSX.Element {

    const popUpContext = usePopUpContext()

    const cardInfoForPopUp = [
        {
            projectID: 1,
            projectTitle: "Uni Final Year Project",
            projectVideo: '../assets/videos/SIAGPU_Eplainer.mp4',
            projectDescription: ''
        }
    ]

    const cardClicked = (projectVideo:string): void => {
        if(projectVideo.length > 0) {
            popUpContext.setDisplayComponent?.(
                <CardPopUpVideo/>
            )
        }
        else {
            popUpContext.setDisplayComponent?.(
                <CardPopUp/>
            )
        }
        popUpContext.showPopUp?.()
    }
    return (
        <div className="card" onClick={cardClicked(p)}>
            <h3 className="title">{props.title}</h3>

            <div className="bar">
                <div className="emptyBar"></div>
                <div className="filledBar"></div>
            </div>

            {/* <div className="circle">
                    <svg version="1.1" xmlns="http://www.w3.org/2000/svg">
                    <circle className="stroke" cx="60" cy="60" r="50"/>
                    </svg>
                </div> */}

            <p className="cardDescription">
                {props.description}
            </p>

            <p className="clickMore">Click Card For More Info</p>

        </div>
    )
}

export default Card
