import { useEffect, useRef } from "react"
import '../../assets/homeItems.css'
import KUTE from 'kute.js'
// import { Options } from 'kute.js'

function HomeItems(): JSX.Element {

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach((entry) => {
                console.log(entry)
                // entry.target.classList.toggle('show', entry.isIntersecting);
                if (entry.isIntersecting) {
                    entry.target.classList.add('show');
                } else {
                    entry.target.classList.remove('show');
                }
            });
        });

        const hiddenElement = document.querySelectorAll('.hidden');
        hiddenElement.forEach((el) => observer.observe(el));

        

    }, []);

    useEffect(() => {
        const tween = KUTE.fromTo(
            '#blob1',
            { path: '#blob1' },
            { path: '#blob2' },
            { repeat: 999, duration:3000, yoyo: true }
        );
        tween.start();
    }, []);

    return (
        <>
            <section className="hidden home-section background-grey">

                <div className="item1">Learn More About Me</div>
            </section>
            <div className="spacer layer2 flip"></div>
            <section className="default home-section pastel-white">
                <div className="item2">Projectssss</div>
                <svg 
                    id="visual" 
                    viewBox="0 0 960 300" 
                    width="960" 
                    height="300" 
                    xmlns="http://www.w3.org/2000/svg" 
                    version="1.1"
                >
                    <g transform="translate(482.622285729826 124.79293557540392)">
                        <path id="blob1"
                            d="M55.9 -49.3C75.1 -36.8 95.1 -18.4 97.2 2.1C99.3 22.6 83.6 45.3 64.4 70.3C45.3 95.3 22.6 122.6 0.8 121.8C-21 121 -42 92 -62.1 67C-82.3 42 -101.6 21 -102.6 -0.9C-103.5 -22.9 -86.1 -45.7 -65.9 -58.2C-45.7 -70.7 -22.9 -72.9 -2.2 -70.6C18.4 -68.4 36.8 -61.8 55.9 -49.3" 
                            fill="#778899"
                        ></path>
                    </g>

                    <g transform="translate(472.03314579181585 143.4301307413046)">
                        <path id="blob2"
                            d="M92 -83.2C113.4 -70.7 120.7 -35.4 113.5 -7.2C106.3 21 84.6 42 63.3 61.5C42 81 21 99 -2.9 101.9C-26.9 104.9 -53.7 92.7 -71.9 73.2C-90.1 53.7 -99.5 26.9 -100.2 -0.7C-101 -28.3 -92.9 -56.6 -74.7 -69.1C-56.6 -81.6 -28.3 -78.3 3.5 -81.8C35.4 -85.4 70.7 -95.7 92 -83.2" 
                            fill="#778899"
                        ></path>
                    </g>
                </svg>
            </section>
            <div className="spacer layer2 "></div>
            <section className="default home-section bubble header-grey">
                <div className="item3">Contact Info</div>
            </section>
        </>
    )
}

export default HomeItems
